#include <Gamebuino-Meta.h>

#include "GameSetup.h"

#include "Constants.h"

const bool GameSetup::CHOOSED       = true;
const bool GameSetup::GO_TO_BACK    = true;

const uint16_t GameSetup::BUTTON_RANDOM_TOKEN_FRAME_INDEX           = 0;
const uint16_t GameSetup::BUTTON_PLAYER_ONE_IS_YELLOW_FRAME_INDEX   = 1;
const uint16_t GameSetup::BUTTON_PLAYER_ONE_IS_RED_FRAME_INDEX      = 2;
const uint16_t GameSetup::CURRENT_BUTTON_FRAME_INDEX                = 3;

GameSetup::GameSetup() {
  this->reset();
}

void GameSetup::manageCommands() {
  if(gb.buttons.pressed(BUTTON_A)) {
    this->isChoosedFlag = GameSetup::CHOOSED;
  } else if(gb.buttons.pressed(BUTTON_B)) {
    this->isGoToBackFlag = GameSetup::GO_TO_BACK;
  } else if(gb.buttons.pressed(BUTTON_UP)) {
    if(this->currentColorForPlayerOne == RED_TOKEN) {             this->currentColorForPlayerOne = YELLOW_TOKEN; }
    else if(this->currentColorForPlayerOne == RANDOM_TOKEN) {     this->currentColorForPlayerOne = RED_TOKEN; }
    else if(this->currentColorForPlayerOne == YELLOW_TOKEN) {     this->currentColorForPlayerOne = RANDOM_TOKEN; }
  } else if(gb.buttons.pressed(BUTTON_DOWN)) {
    if(this->currentColorForPlayerOne == RED_TOKEN) {             this->currentColorForPlayerOne = RANDOM_TOKEN; }
    else if(this->currentColorForPlayerOne == RANDOM_TOKEN) {     this->currentColorForPlayerOne = YELLOW_TOKEN; }
    else if(this->currentColorForPlayerOne == YELLOW_TOKEN) {     this->currentColorForPlayerOne = RED_TOKEN; }
  }
}

void GameSetup::rendering() const {
  this->renderingAllButtons();
  this->renderingCommandsText();
}

void GameSetup::reset() {
  this->currentColorForPlayerOne = RANDOM_TOKEN;
  this->isChoosedFlag = ( ! GameSetup::CHOOSED );
  this->isGoToBackFlag = ( ! GameSetup::GO_TO_BACK );
}

void GameSetup::renderingAllButtons() const {
  int16_t xButton = 51;
  int16_t yMargin = 2;
  int16_t buttonHeight = 32;
  //
  // Afficher bouton P1 = Rouge / P2 = Jaune
  int16_t yButton1 = yMargin;
  this->renderingOneButton(xButton, yButton1, GameSetup::BUTTON_PLAYER_ONE_IS_RED_FRAME_INDEX);
  //
  // Afficher bouton Couleur alÃ©atoire
  int16_t yButton2 = yButton1 + buttonHeight + yMargin;
  this->renderingOneButton(xButton, yButton2, GameSetup::BUTTON_RANDOM_TOKEN_FRAME_INDEX);
  //
  // Afficher bouton P1 = Jaune / P2 = Rouge
  int16_t yButton3 = yButton2 + buttonHeight + yMargin;
  this->renderingOneButton(xButton, yButton3, GameSetup::BUTTON_PLAYER_ONE_IS_YELLOW_FRAME_INDEX);
  //
  // Afficher choix actuel
  int16_t yCurrentButton = yButton2; // RANDOM_TOKEN
  switch( this->getCurrentColorForPlayerOne() ) {
    case RED_TOKEN:     yCurrentButton = yButton1;      break;
    case YELLOW_TOKEN:  yCurrentButton = yButton3;      break;
  }
  this->renderingOneButton(xButton, yCurrentButton, GameSetup::CURRENT_BUTTON_FRAME_INDEX);
}

