void GameSetup::renderingCommandsText() const {
  char changeChoiceText[] = "<Up> or <Down> to change choice";
  size_t lengthChangeChoiceText = sizeof(changeChoiceText) / sizeof(*changeChoiceText);
  char abButtonsText[] = "<A> to valid or <B> go to back";
  size_t lengthAbButtonsText = sizeof(abButtonsText) / sizeof(*abButtonsText);
  gb.display.setFontSize(1);
  uint8_t w = gb.display.getFontWidth();
  uint8_t h = gb.display.getFontHeight();
  gb.display.setColor(MY_GREY);
  //
  // Afficher le texte changement choix
  int16_t xT1 = ( .5 * ( SCREEN_WIDTH - ( w * lengthChangeChoiceText ) ) );
  int16_t y = ( SCREEN_HEIGHT - ( 2.5 * h ) );
  gb.display.setCursor(xT1, y);
  gb.display.print(changeChoiceText);
  //
  // Afficher le texte validation / annulation
  int16_t xT2 = ( .5 * ( SCREEN_WIDTH - ( w * lengthAbButtonsText ) ) );
  y += h;
  gb.display.setCursor(xT2, y);
  gb.display.print(abButtonsText);
}

const uint8_t GameSetup::getCurrentColorForPlayerOne() const {    return this->currentColorForPlayerOne; }
const bool GameSetup::isChoosed() const {                         return this->isChoosedFlag; }
const bool GameSetup::isGoToBack() const {                        return this->isGoToBackFlag; }
