const int16_t MenuUI::computePositionForNavigationItem(const uint8_t itPage, const bool isReverseImageFlag) const {
  int16_t xMarginItem = 1;
  if( isReverseImageFlag == MenuUI::IS_REVERSE_IMAGE) {
    xMarginItem = 2;
  }
  const int16_t X_OFFSET_ITEM = 6 + xMarginItem;
  const int16_t SIZE_ITEM = (6 + 2);
  const int16_t WIDTH_BAR = this->nbItems * SIZE_ITEM;
  const int16_t X_RELATIVE_POSITION = ( .5 * (SCREEN_WIDTH - WIDTH_BAR) );
  int16_t xTotalOffset = 0;
  if(itPage > 0) {
    xTotalOffset = ( SIZE_ITEM * ( itPage - 1 ) + X_OFFSET_ITEM );
  }
  return ( X_RELATIVE_POSITION + xTotalOffset );
}

void MenuUI::renderingNavigation() const {
  const uint8_t LEFT_ARROW_FRAME_INDEX        = 0;
  const uint8_t OTHER_POSITION_FRAME_INDEX    = 1;
  const uint8_t CURRENT_POSITION_FRAME_INDEX  = 2;

  for(uint8_t itPage = 0 ; itPage < this->nbItems ; itPage++) {
    bool isReverseImageFlag = ( ! MenuUI::IS_REVERSE_IMAGE );
    uint8_t frameIndex = CURRENT_POSITION_FRAME_INDEX;
    if(itPage != this->currentPageIndex) {
      if(itPage == 0) {
        // Left arrow
        frameIndex = LEFT_ARROW_FRAME_INDEX;
      } else if(itPage == (this->nbItems - 1)) {
        // Right arrow
        frameIndex = LEFT_ARROW_FRAME_INDEX;
        isReverseImageFlag = MenuUI::IS_REVERSE_IMAGE;
      } else {
        // Other position
        frameIndex = OTHER_POSITION_FRAME_INDEX;
      }
    }
    //
    // Compute xPosition
    const int16_t xPosition = this->computePositionForNavigationItem(itPage, isReverseImageFlag);
    //
    // Current rendering item
    this->renderingUnitNavigation(frameIndex, isReverseImageFlag, xPosition);
  }
}

void MenuUI::renderingUnitNavigation(const uint8_t aFrameIndex, const bool isReverseImageFlag, const int16_t xPosition) const {
  static const uint8_t NAVIGATION_IMAGES[] = {

      // metadata

      6,    // frame width
      6,    // frame height
      0x03, // frames (lower byte)
      0x00, // frames (upper byte)
      0,    // frame loop
      0x0,  // transparent color
      1,    // indexed color mode

      // colormap

      // frame 1/3
      0x00, 0x00, 0x22,
      0x00, 0x22, 0x22,
      0x02, 0x22, 0x22,
      0x00, 0x22, 0x22,
      0x00, 0x00, 0x22,
      0x00, 0x00, 0x00,

      // frame 2/3
      0x00, 0x22, 0x20,
      0x02, 0x22, 0x22,
      0x02, 0x22, 0x22,
      0x02, 0x22, 0x22,
      0x00, 0x22, 0x20,
      0x00, 0x00, 0x00,

      // frame 3/3
      0x00, 0x22, 0x20,
      0x02, 0x20, 0x22,
      0x02, 0x00, 0x02,
      0x02, 0x20, 0x22,
      0x00, 0x22, 0x20,
      0x00, 0x00, 0x00

  };

  Image navigationImg(NAVIGATION_IMAGES);
  navigationImg.setFrame(aFrameIndex);

  int16_t item_size = 6; // height and width
  int16_t reverseFactor = ( (isReverseImageFlag == MenuUI::IS_REVERSE_IMAGE) ? -1 : 1 );
  gb.display.drawImage(
    xPosition,                          // x-pos
    (SCREEN_HEIGHT - (item_size * 2) ), // y-pos
    navigationImg,                      // image
    reverseFactor * item_size,          // width
    item_size                           // height
  );
}

const bool MenuUI::isItemSelected() const {   return (this->isItemSelectedFlag == MenuUI::ITEM_SELECTED); }

const bool MenuUI::isPlayItem() const {             return ( this->getCurrentItemIndex() == MenuUI::PLAY_ITEM_INDEX ); }
const bool MenuUI::isPlayTwoPlayerItem() const {    return ( this->getCurrentItemIndex() == MenuUI::PLAY_2P_ITEM_INDEX ); }
const bool MenuUI::isPlayOnePlayerItem() const {    return ( this->getCurrentItemIndex() == MenuUI::PLAY_1P_ITEM_INDEX ); }
const bool MenuUI::isSettingsItem() const {         return ( this->getCurrentItemIndex() == MenuUI::SETTINGS_ITEM_INDEX ); }
