#include <Gamebuino-Meta.h>

#include "MenuUI.h"

#include "Constants.h"

const bool MenuUI::ITEM_SELECTED            = true;

const bool MenuUI::IS_REVERSE_IMAGE         = true;

const uint8_t MenuUI::NO_ITEM_INDEX         = 0;
const uint8_t MenuUI::PLAY_ITEM_INDEX       = 1;
const uint8_t MenuUI::PLAY_2P_ITEM_INDEX    = 2;
const uint8_t MenuUI::PLAY_1P_ITEM_INDEX    = 3;
const uint8_t MenuUI::SETTINGS_ITEM_INDEX   = 4;
const uint8_t MenuUI::HOME_ITEM_INDEX       = 5;
const uint8_t MenuUI::CREDITS_ITEM_INDEX    = 6;

const int16_t MenuUI::HOME_ICON_INDEX       = 0;
const int16_t MenuUI::PAUSE_ICON_INDEX      = 1;

MenuUI::MenuUI() : 
  currentPageIndex(0),
  iconFrameIndex(MenuUI::HOME_ICON_INDEX)
{
  this->reset();
}

void MenuUI::initialize(uint8_t * anItems, size_t aNbItems, int16_t anIconFrameIndex) {
  this->items = anItems;
  this->nbItems = aNbItems;
  this->iconFrameIndex = anIconFrameIndex;
}

void MenuUI::reset() {
  this->isItemSelectedFlag = ( ! MenuUI::ITEM_SELECTED );
}

void MenuUI::resetCurrentPageIndex() {  this->currentPageIndex = 0; }

void MenuUI::manageCommands() {
  if(gb.buttons.pressed(BUTTON_LEFT)) {
    if(this->currentPageIndex > 0) {
      this->currentPageIndex--;
    }
  } else if(gb.buttons.pressed(BUTTON_RIGHT)) {
    uint8_t nextPageIndex = this->currentPageIndex;
    nextPageIndex++;
    if(nextPageIndex < this->nbItems) {
      this->currentPageIndex = nextPageIndex;
    }
  } else if(gb.buttons.pressed(BUTTON_A)) {
    this->isItemSelectedFlag = MenuUI::ITEM_SELECTED;
  }
}



void MenuUI::rendering() const {
  this->renderingIcon();
  this->renderingItem();
  this->renderingNavigation();
  this->renderingText();
}

const uint8_t MenuUI::getCurrentItemIndex() const {
  uint8_t currentItemIndex = MenuUI::NO_ITEM_INDEX;
  if(this->nbItems > 0) {
    currentItemIndex = this->items[ this->currentPageIndex ];
  }
  return currentItemIndex;
}

void MenuUI::renderingText() const {
  char selectText[] = "Press <A> to select";
  size_t length = sizeof(selectText) / sizeof(*selectText);
  gb.display.setFontSize(1);
  uint8_t w = gb.display.getFontWidth();
  uint8_t h = gb.display.getFontHeight();
  gb.display.setCursor( .5 * ( SCREEN_WIDTH - ( w * length ) ), 5);
  gb.display.setColor(MY_GREY);
  gb.display.print(selectText);
}

